const LineChart: React.FC<{ width: number; height: number; data: number[] }> = ({
  width,
  height,
  data,
}) => {
  const max = Math.max(...data) || 1;
  const min = 0;
  const pad = 24;
  const w = width - pad * 2;
  const h = height - pad * 2;
  const stepX = w / (data.length - 1);
  const points = data
    .map((v, i) => {
      const x = pad + i * stepX;
      const y = pad + h - ((v - min) / (max - min)) * h;
      return `${x},${y}`;
    })
    .join(" ");
  return (
    <svg width={width} height={height} style={{ display: "block" }}>
      {[0.25, 0.5, 0.75, 1].map((g, idx) => (
        <line
          key={idx}
          x1={pad}
          y1={pad + g * h}
          x2={pad + w}
          y2={pad + g * h}
          stroke="#1f2937"
        />
      ))}
      <rect x={pad} y={pad} width={w} height={h} fill="none" stroke="#1f2937" />
      <polyline
        fill="none"
        stroke="#60a5fa"
        strokeWidth={2.5}
        points={points}
      />
      {data.map((v, i) => {
        const x = pad + i * stepX;
        const y = pad + h - (v / max) * h;
        return <circle key={i} cx={x} cy={y} r={3.5} fill="#60a5fa" />;
      })}
    </svg>
  );
};

/* ÎèÑÎÑõ Í∑∏ÎûòÌîÑ */
const DonutChart: React.FC<{
  size: number;
  thickness?: number;
  values: { label: string; value: number; color: string }[];
}> = ({ size, thickness = 34, values }) => {
  const total = values.reduce((a, b) => a + b.value, 0) || 1;
  const radius = (size - thickness) / 2;
  const cx = size / 2,
    cy = size / 2;
  let acc = 0;
  return (
    <svg width={size} height={size}>
      <circle
        cx={cx}
        cy={cy}
        r={radius}
        fill="none"
        stroke="#334155"
        strokeWidth={thickness}
      />
      {values.map((seg, idx) => {
        const frac = seg.value / total;
        const dash = 2 * Math.PI * radius * frac;
        const gap = 2 * Math.PI * radius - dash;
        const offset = -acc * 2 * Math.PI * radius;
        acc += frac;
        return (
          <circle
            key={idx}
            cx={cx}
            cy={cy}
            r={radius}
            fill="none"
            stroke={seg.color}
            strokeWidth={thickness}
            strokeDasharray={`${dash} ${gap}`}
            strokeDashoffset={offset}
            strokeLinecap="round"
          />
        );
      })}
      <text
        x={cx}
        y={cy + 6}
        textAnchor="middle"
        fontSize={22}
        fill="#e5e7eb"
        fontWeight={800}
      >
        Attacks
      </text>
    </svg>
  );
};

/* Í∞ÑÎã® Îã¨Î†• ÌåùÏóÖ */
const CalendarPopup: React.FC<{
  selectedDate: Date;
  onSelect: (date: Date) => void;
}> = ({ selectedDate, onSelect }) => {
  const [currentMonth, setCurrentMonth] = useState(
    new Date(selectedDate.getFullYear(), selectedDate.getMonth(), 1)
  );

  const year = currentMonth.getFullYear();
  const month = currentMonth.getMonth();

  const daysInMonth = new Date(year, month + 1, 0).getDate();
  const startDay = new Date(year, month, 1).getDay();

  const days: (number | null)[] = Array(startDay)
    .fill(null)
    .concat(Array.from({ length: daysInMonth }, (_, i) => i + 1));

  return (
    <div
      style={{
        position: "absolute",
        right: 0,
        marginTop: 8,
        background: "#111827",
        padding: 12,
        borderRadius: 8,
        zIndex: 100,
      }}
    >
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 8 }}>
        <button onClick={() => setCurrentMonth(new Date(year, month - 1, 1))}>{"<"}</button>
        <div>
          {currentMonth.toLocaleString("default", { month: "long" })} {year}
        </div>
        <button onClick={() => setCurrentMonth(new Date(year, month + 1, 1))}>{">"}</button>
      </div>
      <div style={{ display: "grid", gridTemplateColumns: "repeat(7, 1fr)", gap: 4 }}>
        {["Su", "Mo", "Tu", "We", "Th", "Fr", "Sa"].map((d) => (
          <div key={d} style={{ textAlign: "center", fontSize: 12, opacity: 0.7 }}>
            {d}
          </div>
        ))}
        {days.map((day, idx) => (
          <div
            key={idx}
            style={{
              textAlign: "center",
              padding: 6,
              cursor: day ? "pointer" : "default",
              background:
                day === selectedDate.getDate() &&
                month === selectedDate.getMonth() &&
                year === selectedDate.getFullYear()
                  ? "#2563eb"
                  : "transparent",
              borderRadius: 6,
            }}
            onClick={() => day && onSelect(new Date(year, month, day))}
          >
            {day}
          </div>
        ))}
      </div>
    </div>
  );
};

